INSERT INTO `faqs` (`id`, `question`, `answer`, `slug`, `status`, `faq_category_id`, `created_at`, `updated_at`) VALUES
(1, 'شروع با ایسام', '<p>سایت ایسام خریداران و فروشندگان کل کشور را به یكدیگر معرفی و متصل كرده و قادر می‌سازد تا با آرامش خاطر با یکدیگر به تجارت بپردازند!<br>\n<br>\nروش كار سایت ایسام:<br>\nفروشنده محصول، جهت ثبت کالا در سایت اقدام می کند. این کالا می‌تواند فیزیکی یا مجازی، از عتیقه‌جات تا لوازم خانه و یا از كتاب گرفته تا تجهیزات ورزشی باشد. سپس فروشنده روش فروش آن را به صورت مزایده و یا فروش با قیمت ثابت انتخاب می‌كند.</p>\n\n<ol>\n	<li>در یك مزایده آنلاین، پیشنهاددهی با مبلغی كه فروشنده مشخص كرده، آغاز می‌شود و طی چند روز معین در سایت ایسام باقی‌ می‌ماند. در این مدت خریداران پیشنهادهای خود را برای خرید آن كالا ارایه می‌نمایند. پس از پایان مزایده، خریداری كه بالاترین پیشنهاد را ارایه داده ‌است برنده می‌شود و کالا را خریداری می‌كند.</li>\n	<li>هنگام عرضه كالاها به صورت فروش با قیمت ثابت، اولین خریداری كه مبلغ تعیین شده توسط فروشنده را می‌پذیرد می‌تواند كالای مورد نظر را به سبد خرید اضافه کرده و خریداری نماید.</li>\n</ol>', 'شروع-با-ایسام', 1, 1, '2022-06-24 15:49:54', '2022-06-24 15:49:54'),
(2, 'روش عضو شدن', '<p>ثبت نام در سایت ایسام خیلی ساده است و طی چند مرحله می توانید ثبت نام كنید:<br>\n<br>\n1- روی \"ورود / ثبت نام\" کلیک کنید</p>\n\n<p>2- شماره موبایل خود را وارد کنید<br>\n3- کد ارسال شده را وارد کنید<br>\n4- رمز عبور را انتخاب کنید</p>', 'روش-عضو-شدن', 1, 1, '2022-06-24 16:03:24', '2022-06-24 16:03:24'),
(3, 'روش انتخاب رمز عبور', '<ul>\n	<li>رمزهای انتخابی حساس به کوچک و یا بزرگ بودن حروف می باشند. مثلا \"password\" و \"PASSWORD\" دو رمز متفاوت می باشند. هنگامی که رمز خود را انتخاب می کنید، کوچک بودن و یا بزرگ بودن حروف را به یاد داشته باشید.</li>\n	<li>از رمز انتخابی خود با دقت محافظت کنید. به یاد داشته باشید، هر شخصی که رمز ورود شما را بداند، می تواند وارد حساب کاربری شما شود.</li>\n	<li>از ترکیب اعداد و حروف برای انتخاب رمز استفاده کنید. به عنوان مثال salamsam26</li>\n	<li>از ترکیب چندین واژه و عدد بدون فاصله گذاری استفاده نمایید. مانند mastersam84</li>\n	<li>به هیچ وجه از یک کلمه تنها که به راحتی در هر دیکشنری یافت می شود استفاده ننمایید. مانند manager یا bamboo</li>\n	<li>از اطلاعات شخصی خود که به سادگی قابل دسترس بوده و یا قابل حدس می باشد، استفاده ننمایید. مانند نام، شماره تلفن و یا تاریخ تولد خود.</li>\n	<li>از آدرس ایمیل خود به عنوان رمز استفاده ننمایید.</li>\n	<li>از انتخاب رمزهای متداول و قابل حدس جلوگیری کنید. مانند 123456 و یا ABCDEF</li>\n</ul>', 'روش-انتخاب-رمز-عبور', 1, 1, '2022-06-24 16:03:56', '2022-06-24 16:05:50'),
(4, 'چگونه خرید کنم و در مزایدات شرکت کنم؟', '<p><a href=\"https://esam.ir/Service/Help.aspx?order=2_1#C21\">مرحله 1: ثبت نام كنید</a></p>\n\n<p><br>\nاگر قبلا <a href=\"https://esam.ir/Service/Help.aspx?order=1_2\">ثبت نام</a> کرده اید، روی ورود کلیک کنید و نام کاربری و رمز عبور را وارد کنید.<br>\n(برای خروج از بخش راهنما و ورود به حساب کاربری خود <a href=\"https://esam.ir/signInUser.aspx\">اینجا</a> را کلیک کنید.)<br>\n<img alt=\"لینک ورود\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-c2.jpg\"><br>\nدر این صفحه ایمیل، شماره تلفن و رمز عبور را وارد کنید. جهت فعالیت در سایت حتما باید ابتدا توافق نامه کاربری سایت را به صورت کامل مطالعه کرده و پس از آن با تیک زدن در کنار جمله \" قبول توافق نامه کاربری \" شرایط سایت را قبول نمایید. در صورت عدم قبول توافق نامه از عضویت شما در سایت جلوگیری خواهد شد. در پایان کد امنیتی خواسته شده را وارد کنید. (برای خروج از بخش راهنما و ثبت نام خود <a href=\"https://esam.ir/SignInUser.aspx?reg=1\">اینجا</a> را کلیک کنید.)</p>\n\n<p> </p>\n\n<p>روش یک:<br>\nالف) در بالای صفحه اول روی خرید کلیک کنید.<br>\n<img alt=\"لینک خرید\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-c3.jpg\"><br>\nب) وارد صفحه خرید می شوید. بر روی گروه مورد نظر خود کلیک کنید و زیرگروه مورد نظر را انتخاب کنید. (برای خروج از بخش راهنما و ورود به صفحه گروه های مختلف کالا <a href=\"https://esam.ir/purchase/purchasefirst.aspx\">اینجا</a> را کلیک کنید.)<br>\n<br>\n<img alt=\"گروه کالاها\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-c4.jpg\"><br>\nروش دو:<br>\nدر کادر <a href=\"https://esam.ir/Service/Help.aspx?order=2_2\">جستجوی</a> بالای صفحه، نام کالا را جستجو کنید، سپس در سمت راست صفحه نتایج جستجو، از گروه های نمایش داده شده، گروه مد نظر خود را انتخاب نمایید.<br>\n<img alt=\"جستجو\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-c5.jpg\"><br>\nروش سه:<br>\nدر سمت راست صفحه اول، روی گروه کالای مورد نظر کلیک کنید. مثلا برای خرید گوشی موبایل، ابتدا نشانگر موس را روی گروه موبایل و تبلت نگه دارید، سپس از منوی باز شده موبایل را انتخاب کنید.<br>\n<img alt=\"لیست کالاها\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-c6.jpg\"><br>\nدر صورت کلیک روی هر سر گروه، تمام زیرگروه های آن نمایش داده می شود.<br>\n<img alt=\"لیست کالاها\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-c66.jpg\"></p>\n\n<p> \n</p>\n\n<p><br>\n </p>\n', 'چگونه-خرید-کنم-و-در-مزایدات-شرکت-کنم', 1, 3, '2022-06-24 16:05:39', '2022-06-24 16:07:44'),
(5, 'چگونه بفروشم؟', '<p><strong>چگونه کالا برای فروش ثبت کنم؟</strong><br>\n<strong>مرحله 1: روی دکمه \"فروش کالا\" که در بالای تمام صفحات ایسام است، کلیک کنید.</strong><br>\n<img alt=\"دکمه فروش\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc38.jpg\"><br>\nاگر در ایسام ثبت نام نکرده اید و یا داخل حساب کاربری خود نیستید، صفحه ورود / ثبت نام می آید.<br>\n<img alt=\"ورود ثبت نام\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc39.jpg\"><br>\nبرای دریافت راهنمایی در مورد ثبت نام <a href=\"https://esam.ir/Service/Help.aspx?order=1_2\">اینجا</a> کلیک کنید و از شماره 2 به بعد را مطالعه کنید.<br>\nاگر قبلا ثبت نام کرده اید، شماره موبایل خود را وارد کنید و در مرحله بعدی رمز عبوری که انتخاب کرده اید را وارد کنید.<br>\n<img alt=\"وارد کردن شماره موبایل\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc34.jpg\"><br>\n<strong>مرحله 2: عنوان کالا را برای انتخاب گروه وارد کنید.</strong><br>\nعنوان کالا مانند لپ‌تاپ، کیف، دوربین، تمبر و ... را وارد کنید تا گروه کالای شما نمایش داده شود. سپس گروه مورد نظر را انتخاب کنید.<br>\n<img alt=\"عنوان کالا\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc40.jpg\"><br>\nگروه بندی کالا به این جهت مهم است که به خریدار کمک می‌کند سریع تر و راحت تر کالای شما را در سایت پیدا کند.<br>\n<br>\n<strong>پیدا نکردن گروه مورد نظر</strong><br>\nاگر در بین گروه های پیشنهادی، گروه مد نظر خود را پیدا نکردید، بر روی دکمه \"مشاهده تمام گروه ها\" کلیک کنید.<br>\nدر این قسمت لیست تمام گروه ها نشان داده می شود و با کلیک بر روی هر گروه به زیر گروه های آن دسترسی خواهید داشت.<br>\n<img alt=\"مشاهده تمام گروه ها\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc41.jpg\"><br>\n<br>\n<strong>گروه‌های استفاده شده</strong><br>\nبرای فروشندگانی است که پیش از این کالا ثبت کرده اند و می توانند از همان گروه های قبلی خودشان استفاده کنند. با کلیک بر روی هر کدام می توانید کالای خود را در آن گروه ثبت کنید.<br>\n<img alt=\"گروه‌های استفاده شده\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc42.jpg\"><br>\nبرای خروج از راهنما و فروش کالا <a href=\"https://esam.ir/sell/SellFirst.aspx\">اینجا</a> کلیک کنید.<br>\n<br>\n<strong>مرحله 3: صفحهٔ وارد کردن مشخصات کالا</strong><br>\nقبل از ثبت کالا در سایت، مطمئن شوید که عرضه آن با قوانین سایت منطبق بوده و جزو <a href=\"https://esam.ir/Service/Help.aspx?order=8_4\">فعالیت‌ها، مبادلات و کالاهای ممنوعه</a> نباشد.<br>\n<br>\nتوجه: چنانچه می خواهید یک کالای مجازی برای فروش ثبت کنید، بخش <a href=\"https://esam.ir/Service/Help.aspx?order=3_2\">فروش کالای مجازی</a> را مطالعه کنید.<br>\nهنگام انتخاب گروه هایی که هم ماهیت فیزیکی و هم مجازی دارند (مانند کتاب)، پنجره ای باز می شود و باید فیزیکی یا مجازی بودن کالا را انتخاب کنید.<br>\n<img alt=\"انتخاب فروش به صورت مجازی\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc43.jpg\"><br>\n<strong>نکته:</strong> فروشنده باید در مورد کالایی که برای فروش می گذارد، اطلاعات کافی و لازم را داشته باشد. همینطور باید نسبت به سلامت کالای خود آگاه باشد و اگر بخشی از آن معیوب است، حتما در جزییات کالا ذکر شود. اگر فروشنده از کارکرد درست آن مطمئن نیست، باید هنگام ثبت آن کالا، وضعیت آن را \"کالای معیوب یا غیر قابل استفاده\" انتخاب کند.<br>\n<br>\n<strong>انتخاب گروه کالا در فروشگاه</strong><br>\nدر صورتی که فروشگاه در ایسام ثبت کرده اید می توانید مشخص کنید این کالا در چه گروهی از فروشگاه شما قرار ‌گیرد.<br>\n<img alt=\"گروه فروشگاه\" src=\"https://esam.ir/images/HomePage/helpIMG/helpIMG-abc44.jpg\"><br>\nبرای ایجاد گروه در فروشگاه به ایسام من / فروشگاه / مدیریت گروه فروشگاه، مراجعه کنید و یا <a href=\"https://esam.ir/MyEsam/Store/StoreCategoryManagment.aspx\">اینجا</a> کلیک کنید.<br>\n<br>\n<a href=\"https://esam.ir/service/Help.aspx?order=3_1#C32\">قسمت اول: شرح کالا</a><br>\n<a href=\"https://esam.ir/service/Help.aspx?order=3_1#C33\">قسمت دوم: عکس های کالا</a><br>\n<a href=\"https://esam.ir/service/Help.aspx?order=3_1#C34\">قسمت سوم: مشخصات و توضیحات</a><br>\n<a href=\"https://esam.ir/service/Help.aspx?order=3_1#C35\">قسمت چهارم: نحوه فروش و قیمت</a><br>\n<a href=\"https://esam.ir/service/Help.aspx?order=3_1#C36\">قسمت پنجم: نحوه ارسال و هزینه</a><br>\n<br>\n<strong>مرحله 4: ثبت نهایی:</strong><br>\nپس از پر کردن تمام موارد فوق، لطفا روی دکمه ثبت نهایی کلیک کنید.<br>\nبا کلیک روی دکمه ثبت نهایی موافقت خود را با <a href=\"https://esam.ir/service/help.aspx\">قوانین و مقررات ایسام</a> اعلام نموده و مسئولیت صحت اطلاعات وارد شده را می‌پذیرید.<br>\nچنانچه می خواهید پیش از ثبت نهایی کالا، موارد ثبت شده را مرور کنید، بر روی دکمه پیش نمایش کلیک کنید.<br>\nهمینطور چنانچه قصد ثبت نهایی کالا را ندارید، می توانید بر روی دکمه \"ذخیره برای بعد\" کلیک کنید تا کالای شما ذخیره شود و بعدا آن را برای فروش در سایت قرار دهید.</p>', 'چگونه-بفروشم', 1, 4, '2022-06-24 16:09:45', '2022-06-24 16:09:45'),
(6, 'توافق نامه کاربری سایت ایسام', '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\n	<tbody>\n		<tr>\n			<td>ه قسمت توافق نامه کاربر سایت \"ایسام\" خوش آمدید. خدماتی که در وب سایت esam.ir در دسترس شما میباشد توسط شرکت تجارت لناسام سهامی خاص ارائه می گردد. در صورت عدم توافق با این شرایط شما قادر به استفاده از خدمات سایت نخواهید بود.</td>\n		</tr>\n		<tr>\n			<td>ماده 1- طرفین توافق نامه</td>\n		</tr>\n		<tr>\n			<td>این توافق نامه فی ما بین شرکت تجارت لناسام ثبت شده به شماره 339393 به نشانی تهران، خیابان ولیعصر، بالاتر از دستگردی (ظفر)، خیابان بابک بهرامی، پلاک ۵۲، طبقه دوم از یک طرف و کاربران اینترنتی اعم از خریداران و فروشندگان از طرف دیگر در جهت تسهیل معاملات و افزایش امنیت در فضای اینترنتی منعقد می‏گردد. شرکت تجارت لناسام از این پس در این توافق نامه ایسام یا سایت نامیده می شود و کلیه حقوق مادی و معنوی آن در اختیار این شرکت قرار دارد.</td>\n		</tr>\n		<tr>\n			<td>ماده 2- موضوع توافق نامه</td>\n		</tr>\n		<tr>\n			<td>‏‎موضوع قرارداد عبارت است از ایجاد و ارائه شبکه‏ای مطمئن که در آن کاربران اینترنت، اعم از خریداران و فروشندگان، با استفاده از تسهیلات سایت اقدام به خرید و فروش اینترنتی می نمایند. کاربران اینترنتی می‏توانند در هر جای ایران، کالای مورد نظر خود را با استفاده از سایت www.esam.ir مورد مبادله قرار دهند.</td>\n		</tr>\n		<tr>\n			<td>ماده 3- مدت اعتبار توافق نامه</td>\n		</tr>\n		<tr>\n			<td>این توافق نامه از هنگامی که کاربر با آن موافقت می نماید تا زمانی که به یکی از دلایل قراردادی یا قانونی فسخ شود معتبر خواهد بود.</td>\n		</tr>\n		<tr>\n			<td>ماده 4- شروط توافق نامه</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-1- شرایط عضویت کاربر</td>\n		</tr>\n		<tr>\n			<td>در مورد اشخاص حقیقی، تنها اشخاص بالای 18 سال که مقیم ایران باشند می‌توانند به عضویت ایسام درآمده و از خدمات آن در مقام خریدار یا فروشنده استفاده نمایند.<br>\n			اشخاص حقوقی که در دفاتر ثبت شرکت‌ها و موسسات غیرتجاری جمهوری اسلامی ایران به ثبت رسیده باشند نیز می‌توانند عضو این سایت شده و از خدمات سایت استفاده نمایند.</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-2- تعیین نام کاربری و رمز عبور</td>\n		</tr>\n		<tr>\n			<td>عضویت در \"ایسام\" رایگان است .جهت استفاده از خدمات سایت، کاربر میبایست هنگام ثبت نام ، نام کاربری و رمز عبور برای خود تعیین نماید. با انتخاب نام کاربری و رمز عبور و تایید آن توسط سایت، کاربر به عضویت سایت درآمده و مسئولیت کلیه امور انجام شده توسط وی در سایت ، بعهده ایشان خواهد بود در غیر این صورت مجاز به استفاده از خدمات ایسام نبوده و هرگونه دسترسی یا استفاده ، به معنای تجاوز به حقوق ایسام بوده که قابلیت پیگرد قانونی خواهد داشت. ایسام از اعضاء خود میخواهد، حتما شناسه کاربری و کلمه عبور خود را در صفحاتی که دارای آدرس ایسام دات آی آر (esam.ir) میباشد، وارد نمایند. بدیهی است در صورت وارد کردن اطلاعات در آدرس هایی بجز آدرسesam.ir مسئولیت و عواقب از دست رفتن اطلاعات و یا سرقت اطلاعات با کاربر مورد نظر میباشد. کاربر متعهد می شود تا رمز عبور مناسبی برای حساب کاربری خود تعیین نموده و به منظور حصول امنیت در دسترسی به حساب کاربری خود، شخصا مسئولیت حفظ و نگهداری آن را به عهده گیرد. در صورت عدم تعهد، سایت در قبال آن مسئولیتی نخواهد داشت. درصورت نیاز به رمز عبور جدید، کاربر موظف می باشد تا همه معیارها و فرایندهای حفاظتی شرکت را برای احراز هویت طی نموده و در صورتیکه نتواند هویت خود را اثبات نماید سایت این حق را برای خود محفوظ می دارد تا از دسترسی جزیی و یا کلی کاربر مربوطه ، جلوگیری نماید. در هنگام انتخاب نام کاربری نباید از اسامی خاص یا مارکهای تجاری استفاده شود. با انتخاب هر گونه نام کاربری که با شئونات اجتماعی، مذهبی، سیاسی و فرهنگی کشور مغایر باشد، سایت مجاز خواهد بود تا نسبت به ابطال آن اقدام نموده و کاربر حق هیچگونه اعتراض نخواهد داشت.<br>\n			تبصره 1- کاربر حق فروش، انتقال و استفاده از نام کاربری دیگران را نداشته و در صورت اثبات این موضوع ، سایت بدون اجازه کاربر حق ابطال آنرا خواهد داشت.<br>\n			تبصره 2- سایت ایسام مجاز است به صورت مستقیم یا غیرمستقیم اطلاعاتی را که برای تأیید هویت كاربر ضروری بوده کسب نماید. پرسش از خود كاربر برای دریافت اطلاعات تکمیلی ، درخواست تأیید پست الکترونیکی ، استعلام و تحقیق در مورد صحت اطلاعات مرتبط با کارت‌ها یا حساب‌های معرفی‌شده ، برخی از مصادیق این امر تلقی می‌شوند.</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-3- اطلاعات مورد نیاز جهت عضویت</td>\n		</tr>\n		<tr>\n			<td>هنگامی که شما برای استفاده از خدمات سایت، ثبت نام می نمایید، اطلاعات تماس شامل نام ، نشانی و شماره تلفن ، آدرس پست الکترونیکی ، آدرس محل سکونت، اطلاعات بانکی شامل شماره کارت یا شماره حسابتان را در اختیار ما قرار می دهید. تا زمانی که شما مشتری ما باقی بمانید، اطلاعات با توجه به وضعیت حساب کاربری، نوع خدمات و کار سیستم، مدیریت می شوند.<br>\n			تبصره 3- کاربران ایسام میبایست هرگونه تغییر تلفن و آدرس خود را از طریق تیکت به اطلاع سایت برسانند. بدیهی است در صورت مشاهده اشکال در آدرس و تلفن، حساب کاربری خاطی، توقیف خواهد شد .<br>\n			تبصره 4- اگر شماره‌ کارت یا شماره حساب كاربر به هر دلیل تغییر نماید، در اسرع وقت موظف خواهد بود اطلاعات مربوطه را در سایت به روزرسانی نموده در غیر این صورت مسئولیتی متوجه سایت نخواهد بود. این اطلاعات در صفحه \"ایسام من\" در قسمت ویرایش مشخصات بانکی قابل دسترس میباشد .<br>\n			تبصره 5- ایسام اطلاعات دریافت شده از اعضا خود را فقط در امور مرتبط معاملاتی استفاده نموده و به هیچ وجه در اختیار سایرین قرار نخواهد داد. همچنین در صورت نیاز به انجام مقاصد تجاری با کسب اجازه از کاربر نسبت به بهره برداری از این اطلاعات، اقدام خواهد نمود .</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-4- امنیت اطلاعات كاربران</td>\n		</tr>\n		<tr>\n			<td>حفاظت از اطلاعات شخصی كاربران برای این سایت بسیار حائز اهمیت می باشد. جهت اطلاع از اقداماتی كه برای محرمانه ماندن اطلاعات کاربران صورت میگیرد، میتوانید <a href=\"https://esam.ir/service/Help.aspx?order=8_2\">حفظ حریم شخصی</a> را که لینک آن در زیر کلیه صفحات سایت موجود است مطالعه فرمایید</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-5- لغو عضویت</td>\n		</tr>\n		<tr>\n			<td>كاربر در هر زمان می تواند با مراجعه به فرم \"تماس با ما\"، درخواست لغو عضویت در ایسام را بدهد. ایسام حساب کاربر مورد نظر را غیرفعال می کند اما از پایگاه داده های خود خارج نمی کند. این کار به منظور نگهداری جزئیات کاربران و تراکنش های آنها، انجام می شود. با لغو عضویت، سایت کلیه‌ وجوه نزد خود را تا تعیین تکلیف نهایی حفظ خواهد کرد و كاربر حتی پس از لغو عضویت نسبت به تعهداتی که پیش از آن در ازای تراکنش‌های ثبت‌شده متقبل شده‌ مسئول خواهد بود.<br>\n			<br>\n			تبصره 6- در صورتی که کاربر، عملی خارج از قوانین و مقررات تعیین شده انجام دهد، سایت مجاز خواهد بود بدون اخطار پیشین عضویت کاربر را به حالت تعلیق در آورده و یا آن را ابطال نماید و طبق قوانین و مقررات با ایشان برخورد خواهد شد. کاربر با قبول این توافق نامه موافقت خود را مبنی بر ایفای تعهدات مندرج در <a href=\"https://esam.ir/service/Help.aspx?order=8_1\">قوانین و مقررات</a> و قبول تمامی مطالب درج شده در <a href=\"https://esam.ir/service/Help.aspx\">راهنما</a> اعلام می دارد.<br>\n			<br>\n			تبصره 7- با قبول این توافق نامه، شرکت مجازاست تا در صورت وقوع هر گونه تقلب، یا کشف احتمال تقلب و کلاهبرداری، عضویت کاربر را معلق و یا ابطال نماید و کاربر حق هیچگونه اعتراضی در این خصوص ندارد .</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-6- ارتباط میان کاربران با یکدیگر و سایت</td>\n		</tr>\n		<tr>\n			<td>ارتباط با کلیه بخشهای سایت از طریق \"تماس با ایسام\" ویا\"درخواست تماس باشما\" که لینک آن در صفحه اول سایت موجود است برقرار می‌گردد. ضمنا در مواقع ضروری سایت از طریق شماره تلفن هر كاربر با وی ارتباط برقرار می نماید. همچنین ارتباط کاربران با یکدیگر از طریق \"پیام های خصوصی\" می باشد و یا از طریق بخش \"سوال از فروشنده\" با یکدیگر ارتباط برقرار می کنند.<br>\n			<br>\n			تبصره 8- بر اساس این توافق نامه كاربر قبول می نماید کلیه اخطارها ، تذکرات و مکاتبات مربوطه، مندرج در سایت و یا ارسال شده به نشانی پست الکترونیک و یا نشانی پستی درج‌شده هنگام تکمیل درخواست عضویت، به رویت وی می‏رسد. در این صورت 24 ساعت پس از درج اخطار بر روی سایت یا ارسال آن به نشانی پست الکترونیک وی ، اخطار، ابلاغ شده تلقی گردیده و در صورت ارسال از طریق پست، اخطار مذکور پس از گذشت سه (۳) روز کاری ابلاغ شده تلقی می‌گردد. اگر كاربر پس از گذشت این مدت از ادامه‌ ارتباط الکترونیکی با ایسام منصرف گردد، سایت مجاز خواهد بود که کلیه حقوق کاربری وی را محدود و یا معلق نماید.<br>\n			<br>\n			تبصره 9- اگر علیرغم تلاش شبانه روزی پرسنل در جهت فعال بودن سایت، در بعضی از موارد به دلایل غیر ارادی و یا فورس ماژور دسترسی 24 ساعته کاربر به سایت مقدور نباشد، با پذیرش این توافقنامه کاربر حق هیچگونه اعتراض و اقامه دعوی در خصوص قطع تماس احتمالی و همچنین زیانها و خسارات احتمالی وارده ناشی از موارد مذکور را نداشته و این حق را از خود سلب می نماید.<br>\n			<br>\n			تبصره 10- طبق این توافق نامه ایسام تمامی پیام های کاربران به یکدیگر را کنترل می کند و پیام هایی که شامل هرگونه اطلاعات تماس، لینک سایر وب سایتها، توهین و تمسخر باشد را حذف و یا ویرایش می نماید.<br>\n			<br>\n			تبصره 11- چنانچه علیرغم تلاش پرسنل ایسام جهت کنترل پیامها، هرگونه پیام شامل توهین، تمسخر و اطلاعات تماس، توسط کاربر دیگری دریافت شود، سایت ایسام هیچ گونه مسئولیتی در قبال پیام ارسال شده و یا دریافت شده ندارد.</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-7- شرح کالا</td>\n		</tr>\n		<tr>\n			<td>برای عرضه کالا می توانید از متن، عکس و گرافیک در گروه های مناسب استفاده کرده و مسئولیت استفاده از هر نوع ابزار فوق که مغایر با شئونات مذهبی، اجتماعی ،سیاسی و فرهنگی کشور باشد منحصرا متوجه کاربر خواهدبود. فروشنده می بایست ضمن تشریح کامل وضعیت کالا، کلیه شرایط مربوط به فروش آنرا نیز اعلام نموده و نوع فروش کالا نیز باید دقیقا در شرح آن درج شود .<br>\n			<br>\n			تبصره 12- سایت توضیحات محصولات، قیمت آن‌ها و آمار مربوط به فروشندگان یا سایر موارد را تأیید نمی‌کند. همچنین ادعا نمی‌کند که نتایج جست‌وجو لزوما کامل، دقیق، قابل‌اعتماد و به‌ روز باشد. این خدمت برای آگاهی بیشتر و کمک به خریداران طراحی شده و به معنای تایید یا تضمین کالاها و خدمات فروشندگان نیست.</td>\n		</tr>\n		<tr>\n			<td>  <img alt=\"\" src=\"https://esam.ir/images/HomePage/Bullet_flesh.gif\"> 4-8- خرید و فروش</td>\n		</tr>\n		<tr>\n			<td>درصورتی که کاربر ، اقدام به فروش کالایی در سایت نماید، باید اطمینان داش